import{_ as t,c as n,o as a,a as e,b as i}from"./app.411b39d2.js";const y=JSON.parse('{"title":"Rationale","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/rationale.md"}'),o={name:"documentation/rationale.md"},s=e("h1",{id:"rationale",tabindex:"-1"},[i("Rationale "),e("a",{class:"header-anchor",href:"#rationale","aria-hidden":"true"},"#")],-1),r=e("p",null,"The continuing success of SRE implementations in diverse organizations is not to be underestimated. However, these implementations do not necessarily enable the SRE community to study and define the SRE practice in a theoretically consistent manner, and this is because these implementations are largely based on hard-earned and organization-specific experiences, therefore they do not guarantee convergence of ideas and essentially do not necessarily solidify into fundamental models that can be consistently utilized to express any aspect of SRE in an organization-agnostic or experience-agnostic manner.",-1),c=e("p",null,"Without these fundamental models, the meaning of SRE is difficult to untangle and will likely have varying degrees of subjective interpretations where its potential consequence is that SRE will likely end up as an organizational culture rather than a robust discipline that can be studied theoretically, taught in universities, and most importantly advance as a computer science field.",-1),l=[s,r,c];function d(h,u,m,p,f,_){return a(),n("div",null,l)}const b=t(o,[["render",d]]);export{y as __pageData,b as default};
